import React, { useState } from 'react';
import { ChevronDown, ChevronUp, Download, Shield, AlertTriangle, CheckCircle, XCircle } from 'lucide-react';
import { RiskScore } from '../types';

interface UserTableProps {
  riskScores: RiskScore[];
}

const UserTable: React.FC<UserTableProps> = ({ riskScores }) => {
  const [sortField, setSortField] = useState<keyof RiskScore>('credit_score');
  const [sortDirection, setSortDirection] = useState<'asc' | 'desc'>('desc');
  const [filterDecision, setFilterDecision] = useState<string>('all');

  const handleSort = (field: keyof RiskScore) => {
    if (field === sortField) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortDirection('desc');
    }
  };

  const filteredAndSortedData = riskScores
    .filter(score => filterDecision === 'all' || score.decision === filterDecision)
    .sort((a, b) => {
      const aVal = a[sortField];
      const bVal = b[sortField];

      if (typeof aVal === 'number' && typeof bVal === 'number') {
        return sortDirection === 'asc' ? aVal - bVal : bVal - aVal;
      }

      const aStr = String(aVal);
      const bStr = String(bVal);
      return sortDirection === 'asc' 
        ? aStr.localeCompare(bStr)
        : bStr.localeCompare(aStr);
    });

  const getRiskLevelColor = (level: string) => {
    switch (level) {
      case 'Low': return 'bg-success-100 text-success-800';
      case 'Medium': return 'bg-warning-100 text-warning-800';
      case 'High': return 'bg-danger-100 text-danger-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getDecisionColor = (decision: string) => {
    switch (decision) {
      case 'Approve': return 'bg-success-100 text-success-800';
      case 'Review': return 'bg-warning-100 text-warning-800';
      case 'Reject': return 'bg-danger-100 text-danger-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const exportToCSV = () => {
    const headers = ['User ID', 'Risk Score', 'Risk Level', 'Decision', 'RBI Compliant', 'Max Loan Amount', 'EMI Ratio', 'Reason Codes', 'RBI Violations'];
    const csvContent = [
      headers.join(','),
      ...filteredAndSortedData.map(score => [
        score.user_id,
        score.risk_score,
        score.risk_level,
        score.decision,
        score.rbi_compliant ? 'Yes' : 'No',
        score.max_loan_amount,
        score.emi_to_income_ratio + '%',
        `"${score.reason_codes.join('; ')}"`,
        `"${score.rbi_violations.join('; ')}"`
      ].join(','))
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'risk_assessment_results.csv';
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const SortIcon = ({ field }: { field: keyof RiskScore }) => {
    if (sortField !== field) return null;
    return sortDirection === 'asc' ? 
      <ChevronUp className="h-4 w-4" /> : 
      <ChevronDown className="h-4 w-4" />;
  };

  return (
    <div className="space-y-4">
      {/* Controls */}
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center space-y-2 sm:space-y-0">
        <div className="flex items-center space-x-4">
          <label className="text-sm font-medium text-gray-700">Filter by Decision:</label>
          <select
            value={filterDecision}
            onChange={(e) => setFilterDecision(e.target.value)}
            className="border border-gray-300 rounded-md px-3 py-1 text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
          >
            <option value="all">All</option>
            <option value="Approve">Approve</option>
            <option value="Review">Review</option>
            <option value="Reject">Reject</option>
          </select>

          <div className="flex items-center space-x-2 text-sm text-gray-600">
            <Shield className="h-4 w-4" />
            <span>RBI Compliant: {riskScores.filter(s => s.rbi_compliant).length}/{riskScores.length}</span>
          </div>
        </div>

        <button
          onClick={exportToCSV}
          className="flex items-center space-x-2 bg-primary-500 text-white px-4 py-2 rounded-lg hover:bg-primary-600 transition-colors"
        >
          <Download className="h-4 w-4" />
          <span>Export CSV</span>
        </button>
      </div>

      {/* Table */}
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white border border-gray-200 rounded-lg">
          <thead className="bg-gray-50">
            <tr>
              <th 
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
                onClick={() => handleSort('user_id')}
              >
                <div className="flex items-center space-x-1">
                  <span>User ID</span>
                  <SortIcon field="user_id" />
                </div>
              </th>
              <th 
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
                onClick={() => handleSort('risk_score')}
              >
                <div className="flex items-center space-x-1">
                  <span>Risk Score</span>
                  <SortIcon field="risk_score" />
                </div>
              </th>
              <th 
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
                onClick={() => handleSort('risk_level')}
              >
                <div className="flex items-center space-x-1">
                  <span>Risk Level</span>
                  <SortIcon field="risk_level" />
                </div>
              </th>
              <th 
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
                onClick={() => handleSort('decision')}
              >
                <div className="flex items-center space-x-1">
                  <span>Decision</span>
                  <SortIcon field="decision" />
                </div>
              </th>
              <th 
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
                onClick={() => handleSort('rbi_compliant')}
              >
                <div className="flex items-center space-x-1">
                  <Shield className="h-3 w-3" />
                  <span>RBI Status</span>
                  <SortIcon field="rbi_compliant" />
                </div>
              </th>
              <th 
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
                onClick={() => handleSort('max_loan_amount')}
              >
                <div className="flex items-center space-x-1">
                  <span>Max Loan</span>
                  <SortIcon field="max_loan_amount" />
                </div>
              </th>
              <th 
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
                onClick={() => handleSort('emi_to_income_ratio')}
              >
                <div className="flex items-center space-x-1">
                  <span>EMI Ratio</span>
                  <SortIcon field="emi_to_income_ratio" />
                </div>
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Details
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {filteredAndSortedData.map((score) => (
              <tr key={score.user_id} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                  {score.user_id}
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="flex items-center">
                    <span className="text-sm font-medium text-gray-900 mr-2">
                      {score.risk_score}
                    </span>
                    <div className="w-16 bg-gray-200 rounded-full h-2">
                      <div
                        className={`h-2 rounded-full ${
                          score.risk_score >= 70 ? 'bg-success-500' :
                          score.risk_score >= 40 ? 'bg-warning-500' : 'bg-danger-500'
                        }`}
                        style={{ width: `${score.risk_score}%` }}
                      ></div>
                    </div>
                  </div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getRiskLevelColor(score.risk_level)}`}>
                    {score.risk_level}
                  </span>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getDecisionColor(score.decision)}`}>
                    {score.decision}
                  </span>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="flex items-center space-x-2">
                    {score.rbi_compliant ? (
                      <div className="flex items-center space-x-1 text-green-600">
                        <CheckCircle className="h-4 w-4" />
                        <span className="text-xs font-medium">Compliant</span>
                      </div>
                    ) : (
                      <div className="flex items-center space-x-1 text-red-600">
                        <XCircle className="h-4 w-4" />
                        <span className="text-xs font-medium">Non-Compliant</span>
                      </div>
                    )}
                  </div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  ₹{(score.max_loan_amount/1000).toFixed(0)}K
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="flex items-center space-x-2">
                    <span className={`text-sm font-medium ${
                      score.emi_to_income_ratio > 50 ? 'text-red-600' : 
                      score.emi_to_income_ratio > 40 ? 'text-yellow-600' : 'text-green-600'
                    }`}>
                      {score.emi_to_income_ratio}%
                    </span>
                    {score.emi_to_income_ratio > 50 && (
                      <AlertTriangle className="h-4 w-4 text-red-500" />
                    )}
                  </div>
                </td>
                <td className="px-6 py-4 text-sm text-gray-500">
                  <div className="space-y-2">
                    <div>
                      <p className="text-xs font-medium text-gray-700 mb-1">Reason Codes:</p>
                      <div className="space-y-1">
                        {score.reason_codes.map((reason, index) => (
                          <div key={index} className="text-xs bg-gray-100 px-2 py-1 rounded">
                            {reason}
                          </div>
                        ))}
                      </div>
                    </div>
                    {score.rbi_violations.length > 0 && (
                      <div>
                        <p className="text-xs font-medium text-red-700 mb-1">RBI Violations:</p>
                        <div className="space-y-1">
                          {score.rbi_violations.map((violation, index) => (
                            <div key={index} className="text-xs bg-red-50 text-red-700 px-2 py-1 rounded border border-red-200">
                              {violation}
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {filteredAndSortedData.length === 0 && (
        <div className="text-center py-8 text-gray-500">
          No data matches the current filter criteria.
        </div>
      )}
    </div>
  );
};

export default UserTable;
