import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft } from 'lucide-react';
import Header from './Header';
import FileUpload from './FileUpload';
import Dashboard from './Dashboard';
import UserTable from './UserTable';
import PageTransition from './PageTransition';
import { RiskScore, DashboardStats } from '../types';

const MainApp: React.FC = () => {
  const navigate = useNavigate();
  const [riskScores, setRiskScores] = useState<RiskScore[]>([]);
  const [dashboardStats, setDashboardStats] = useState<DashboardStats | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isVisible, setIsVisible] = useState(false);

  const handleFileProcessed = (scores: RiskScore[], stats: DashboardStats) => {
    setRiskScores(scores);
    setDashboardStats(stats);
  };

  const handleBackToLanding = () => {
    navigate('/');
  };

  useEffect(() => {
    // Trigger fade-in animation after component mounts
    const timer = setTimeout(() => {
      setIsVisible(true);
    }, 100);
    return () => clearTimeout(timer);
  }, []);

  return (
    <PageTransition>
      <div className={`min-h-screen bg-gray-50 transition-all duration-700 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'}`}>
      {/* Back to Landing Button */}
      <div className="bg-white border-b">
        <div className="container mx-auto px-4 py-2">
          <button
            onClick={handleBackToLanding}
            className="flex items-center text-gray-600 hover:text-primary-500 transition-colors"
          >
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back to Landing
          </button>
        </div>
      </div>

      <Header />

      <main className="container mx-auto px-4 py-8">
        <div className="space-y-8">
          {/* File Upload Section */}
          <div className={`bg-white rounded-lg shadow-md p-6 transition-all duration-500 delay-200 ${isVisible ? 'animate-slideInLeft' : 'opacity-0'}`}>
            <h2 className="text-2xl font-bold text-gray-800 mb-4">
              Upload Transaction Data
            </h2>
            <FileUpload 
              onFileProcessed={handleFileProcessed}
              isLoading={isLoading}
              setIsLoading={setIsLoading}
            />
          </div>

          {/* Dashboard Section */}
          {dashboardStats && (
            <div className={`transition-all duration-500 delay-300 ${isVisible ? 'animate-fadeIn' : 'opacity-0'}`}>
              <Dashboard stats={dashboardStats} riskScores={riskScores} />
            </div>
          )}

          {/* Results Table */}
          {riskScores.length > 0 && (
            <div className={`bg-white rounded-lg shadow-md p-6 transition-all duration-500 delay-400 ${isVisible ? 'animate-slideInRight' : 'opacity-0'}`}>
              <h2 className="text-2xl font-bold text-gray-800 mb-4">
                Risk Assessment Results
              </h2>
              <UserTable riskScores={riskScores} />
            </div>
          )}
        </div>
      </main>
      </div>
    </PageTransition>
  );
};

export default MainApp;
