import React, { useState } from 'react';
import { User, Lock, Mail, Phone, MapPin, Calendar } from 'lucide-react';

interface BorrowerAuthProps {
  onLogin: (borrowerId: string, borrowerData: any) => void;
}

interface BorrowerData {
  id: string;
  name: string;
  email: string;
  phone: string;
  address: string;
  dateOfBirth: string;
  monthlyIncome: number;
  existingDebt: number;
  loanPurpose: string;
  requestedAmount: number;
}

const BorrowerAuth: React.FC<BorrowerAuthProps> = ({ onLogin }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    phone: '',
    address: '',
    dateOfBirth: '',
    monthlyIncome: '',
    existingDebt: '',
    loanPurpose: 'business',
    requestedAmount: ''
  });

  // Mock user database for demo
  const mockUsers = {
    'john@example.com': {
      password: 'password123',
      data: {
        id: 'john',
        name: 'John Doe',
        email: 'john@example.com',
        phone: '+91 9876543210',
        address: 'Mumbai, Maharashtra',
        dateOfBirth: '1990-01-15',
        monthlyIncome: 25000,
        existingDebt: 15000,
        loanPurpose: 'business',
        requestedAmount: 50000
      }
    },
    'jane@example.com': {
      password: 'demo123',
      data: {
        id: 'jane',
        name: 'Jane Smith',
        email: 'jane@example.com',
        phone: '+91 9876543211',
        address: 'Delhi, Delhi',
        dateOfBirth: '1992-03-20',
        monthlyIncome: 35000,
        existingDebt: 8000,
        loanPurpose: 'education',
        requestedAmount: 75000
      }
    },
    'demo@test.com': {
      password: 'demo',
      data: {
        id: 'demo',
        name: 'Demo User',
        email: 'demo@test.com',
        phone: '+91 9876543212',
        address: 'Bangalore, Karnataka',
        dateOfBirth: '1988-07-10',
        monthlyIncome: 45000,
        existingDebt: 20000,
        loanPurpose: 'business',
        requestedAmount: 100000
      }
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (isLogin) {
      // Simulate login - in real app, this would call an API
      const borrowerId = formData.email.split('@')[0];
      const mockBorrowerData: BorrowerData = {
        id: borrowerId,
        name: 'John Doe',
        email: formData.email,
        phone: '+91 9876543210',
        address: 'Mumbai, Maharashtra',
        dateOfBirth: '1990-01-15',
        monthlyIncome: 25000,
        existingDebt: 15000,
        loanPurpose: 'business',
        requestedAmount: 50000
      };
      onLogin(borrowerId, mockBorrowerData);
    } else {
      // Simulate signup
      const borrowerId = formData.email.split('@')[0];
      const newBorrowerData: BorrowerData = {
        id: borrowerId,
        name: formData.name,
        email: formData.email,
        phone: formData.phone,
        address: formData.address,
        dateOfBirth: formData.dateOfBirth,
        monthlyIncome: parseInt(formData.monthlyIncome),
        existingDebt: parseInt(formData.existingDebt || '0'),
        loanPurpose: formData.loanPurpose,
        requestedAmount: parseInt(formData.requestedAmount)
      };
      onLogin(borrowerId, newBorrowerData);
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <div className="max-w-md w-full bg-white rounded-xl shadow-lg p-8">
        <div className="text-center mb-8">
          <div className="bg-blue-100 rounded-full w-16 h-16 flex items-center justify-center mx-auto mb-4">
            <User className="h-8 w-8 text-blue-600" />
          </div>
          <h2 className="text-2xl font-bold text-gray-900">
            {isLogin ? 'Borrower Login' : 'Apply for Loan'}
          </h2>
          <p className="text-gray-600 mt-2">
            {isLogin ? 'Access your loan dashboard' : 'Start your loan application'}
          </p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Email Address
            </label>
            <div className="relative">
              <Mail className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                className="w-full pl-10 pr-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                placeholder="your@email.com"
                required
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Password
            </label>
            <div className="relative">
              <Lock className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
                className="w-full pl-10 pr-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                placeholder="Enter password"
                required
              />
            </div>
          </div>

          {!isLogin && (
            <>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Full Name
                </label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="Your full name"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Phone Number
                </label>
                <div className="relative">
                  <Phone className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                  <input
                    type="tel"
                    name="phone"
                    value={formData.phone}
                    onChange={handleInputChange}
                    className="w-full pl-10 pr-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="+91 9876543210"
                    required
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Address
                </label>
                <div className="relative">
                  <MapPin className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                  <input
                    type="text"
                    name="address"
                    value={formData.address}
                    onChange={handleInputChange}
                    className="w-full pl-10 pr-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="City, State"
                    required
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Date of Birth
                </label>
                <div className="relative">
                  <Calendar className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                  <input
                    type="date"
                    name="dateOfBirth"
                    value={formData.dateOfBirth}
                    onChange={handleInputChange}
                    className="w-full pl-10 pr-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    required
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Monthly Income (₹)
                  </label>
                  <input
                    type="number"
                    name="monthlyIncome"
                    value={formData.monthlyIncome}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="25000"
                    required
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Existing Debt (₹)
                  </label>
                  <input
                    type="number"
                    name="existingDebt"
                    value={formData.existingDebt}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="0"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Loan Purpose
                </label>
                <select
                  name="loanPurpose"
                  value={formData.loanPurpose}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  required
                >
                  <option value="business">Business</option>
                  <option value="education">Education</option>
                  <option value="medical">Medical</option>
                  <option value="agriculture">Agriculture</option>
                  <option value="personal">Personal</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Requested Amount (₹)
                </label>
                <input
                  type="number"
                  name="requestedAmount"
                  value={formData.requestedAmount}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="50000"
                  max="125000"
                  required
                />
                <p className="text-xs text-gray-500 mt-1">Maximum: ₹1,25,000 (RBI Limit)</p>
              </div>
            </>
          )}

          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition duration-200"
          >
            {isLogin ? 'Login to Dashboard' : 'Submit Application'}
          </button>
        </form>

        <div className="mt-6 text-center">
          <button
            type="button"
            onClick={() => setIsLogin(!isLogin)}
            className="text-blue-600 hover:text-blue-700 text-sm font-medium"
          >
            {isLogin ? "Don't have an account? Apply for loan" : 'Already have an account? Login'}
          </button>
        </div>

        {isLogin && (
          <div className="mt-4 p-3 bg-blue-50 rounded-lg">
            <p className="text-xs text-blue-600">
              <strong>Demo:</strong> Use any email to login and explore the borrower dashboard
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default BorrowerAuth;
