import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import { Upload, FileText, AlertCircle, CheckCircle, Shield } from 'lucide-react';
import { RiskScore, DashboardStats, UserData } from '../types';
import { RealisticCreditEngine } from '../utils/realisticCreditEngine';

interface FileUploadProps {
  onFileProcessed: (scores: RiskScore[], stats: DashboardStats) => void;
  isLoading: boolean;
  setIsLoading: (loading: boolean) => void;
}

const FileUpload: React.FC<FileUploadProps> = ({ onFileProcessed, isLoading, setIsLoading }) => {
  const [uploadStatus, setUploadStatus] = useState<'idle' | 'success' | 'error'>('idle');
  const [errorMessage, setErrorMessage] = useState<string>('');

  const processCSVData = (csvText: string): UserData[] => {
    const lines = csvText.trim().split('\n');
    const headers = lines[0].split(',').map(h => h.trim());

    return lines.slice(1).map(line => {
      const values = line.split(',').map(v => v.trim());
      const userData: any = {};

      headers.forEach((header, index) => {
        const value = values[index];
        switch (header) {
          case 'user_id':
            userData.user_id = value;
            break;
          case 'income_inflow':
            userData.income_inflow = parseFloat(value);
            break;
          case 'bill_payments_on_time':
            userData.bill_payments_on_time = parseFloat(value.replace('%', ''));
            break;
          case 'avg_upi_txn':
            userData.avg_upi_txn = parseFloat(value);
            break;
          case 'recharge_freq':
            userData.recharge_freq = parseFloat(value.split('/')[0]);
            break;
          case 'loan_defaulted':
            userData.loan_defaulted = value.toLowerCase() === 'yes';
            break;
          // RBI Compliance fields
          case 'existing_loan_amount':
            userData.existing_loan_amount = parseFloat(value) || 0;
            break;
          case 'household_income':
            userData.household_income = parseFloat(value) || userData.income_inflow;
            break;
          case 'rural_urban_classification':
            userData.rural_urban_classification = value.toLowerCase() === 'rural' ? 'rural' : 'urban';
            break;
          case 'purpose_of_loan':
            userData.purpose_of_loan = value || 'general';
            break;
          case 'requested_amount':
            userData.requested_amount = parseFloat(value) || 50000;
            break;
          case 'proposed_tenure_months':
            userData.proposed_tenure_months = parseInt(value) || 24;
            break;
        }
      });

      // Set defaults for RBI fields if not provided
      userData.existing_loan_amount = userData.existing_loan_amount || 0;
      userData.household_income = userData.household_income || userData.income_inflow;
      userData.rural_urban_classification = userData.rural_urban_classification || 'urban';
      userData.purpose_of_loan = userData.purpose_of_loan || 'general';
      userData.requested_amount = userData.requested_amount || 50000;
      userData.proposed_tenure_months = userData.proposed_tenure_months || 24;

      return userData as UserData;
    });
  };

  const calculateRiskScore = (userData: UserData[]): { scores: RiskScore[], stats: DashboardStats } => {
    const scores: RiskScore[] = userData.map(user => {
      // Feature engineering
      const paymentRatio = user.bill_payments_on_time / 100;
      const incomeStability = Math.min(user.income_inflow / 10000, 1);
      const transactionActivity = Math.min(user.avg_upi_txn / 50, 1);
      const rechargeConsistency = Math.min(user.recharge_freq / 6, 1);

      // RBI Compliance Check
      const rbiProfile: RBIBorrowerProfile = {
        user_id: user.user_id,
        monthly_income: user.income_inflow,
        existing_loan_amount: user.existing_loan_amount || 0,
        household_income: user.household_income || user.income_inflow,
        rural_urban_classification: user.rural_urban_classification || 'urban',
        purpose_of_loan: user.purpose_of_loan || 'general',
        collateral_free: true
      };

      const requestedAmount = user.requested_amount || 50000;
      const tenureMonths = user.proposed_tenure_months || 24;
      const proposedRate = 24; // 24% annual interest rate

      const rbiCompliance = RBIComplianceEngine.checkCompliance(
        rbiProfile,
        requestedAmount,
        proposedRate,
        tenureMonths
      );

      const recommendations = RBIComplianceEngine.getRecommendations(rbiProfile);

      // Calculate EMI to Income Ratio
      const monthlyEMI = (requestedAmount * (proposedRate/100/12) * Math.pow(1 + proposedRate/100/12, tenureMonths)) / 
                        (Math.pow(1 + proposedRate/100/12, tenureMonths) - 1);
      const emiToIncomeRatio = (monthlyEMI / user.income_inflow) * 100;

      // Risk calculation (0-100 scale) with RBI adjustment
      let riskScore = 
        (paymentRatio * 30) +
        (incomeStability * 25) +
        (transactionActivity * 20) +
        (rechargeConsistency * 15) +
        (user.loan_defaulted ? -20 : 10) +
        rbiCompliance.riskAdjustment; // RBI compliance adjustment

      riskScore = Math.max(0, Math.min(100, riskScore));

      // Determine risk level and decision (RBI compliance affects decision)
      let riskLevel: 'Low' | 'Medium' | 'High';
      let decision: 'Approve' | 'Review' | 'Reject';

      // If not RBI compliant, automatically review or reject
      if (!rbiCompliance.isCompliant) {
        if (riskScore >= 60) {
          riskLevel = 'Medium';
          decision = 'Review';
        } else {
          riskLevel = 'High';
          decision = 'Reject';
        }
      } else {
        if (riskScore >= 70) {
          riskLevel = 'Low';
          decision = 'Approve';
        } else if (riskScore >= 40) {
          riskLevel = 'Medium';
          decision = 'Review';
        } else {
          riskLevel = 'High';
          decision = 'Reject';
        }
      }

      // Generate reason codes including RBI violations
      const reasonCodes: string[] = [];
      if (paymentRatio < 0.7) reasonCodes.push('Low payment consistency');
      if (incomeStability < 0.5) reasonCodes.push('Unstable income');
      if (transactionActivity < 0.3) reasonCodes.push('Low transaction activity');
      if (user.loan_defaulted) reasonCodes.push('Previous default history');
      if (emiToIncomeRatio > 50) reasonCodes.push('High EMI-to-income ratio');
      if (!rbiCompliance.isCompliant) reasonCodes.push('RBI compliance issues');
      if (reasonCodes.length === 0) reasonCodes.push('Strong financial profile');

      return {
        user_id: user.user_id,
        risk_score: Math.round(riskScore),
        risk_level: riskLevel,
        decision,
        reason_codes: reasonCodes.slice(0, 3),
        // RBI Compliance fields
        rbi_compliant: rbiCompliance.isCompliant,
        rbi_violations: rbiCompliance.violations,
        max_loan_amount: recommendations.maxRecommendedAmount,
        recommended_tenure: recommendations.recommendedTenure,
        emi_to_income_ratio: Math.round(emiToIncomeRatio * 100) / 100
      };
    });

    // Calculate dashboard stats
    const totalUsers = scores.length;
    const approvedUsers = scores.filter(s => s.decision === 'Approve').length;
    const highRiskCount = scores.filter(s => s.risk_level === 'High').length;
    const mediumRiskCount = scores.filter(s => s.risk_level === 'Medium').length;
    const lowRiskCount = scores.filter(s => s.risk_level === 'Low').length;

    // RBI Compliance Stats
    const compliantUsers = scores.filter(s => s.rbi_compliant).length;
    const totalLoanAmount = scores.reduce((sum, s) => sum + (userData.find(u => u.user_id === s.user_id)?.requested_amount || 50000), 0);

    // Count common violations
    const violationCounts = new Map<string, number>();
    scores.forEach(score => {
      score.rbi_violations.forEach(violation => {
        violationCounts.set(violation, (violationCounts.get(violation) || 0) + 1);
      });
    });

    const commonViolations = Array.from(violationCounts.entries())
      .map(([violation, count]) => ({violation, count}))
      .sort((a, b) => b.count - a.count)
      .slice(0, 3);

    const stats: DashboardStats = {
      total_users: totalUsers,
      approval_rate: (approvedUsers / totalUsers) * 100,
      high_risk_count: highRiskCount,
      medium_risk_count: mediumRiskCount,
      low_risk_count: lowRiskCount,
      // RBI Compliance Stats
      rbi_compliance_rate: (compliantUsers / totalUsers) * 100,
      avg_loan_amount: totalLoanAmount / totalUsers,
      total_compliant_users: compliantUsers,
      common_violations: commonViolations
    };

    return { scores, stats };
  };

  const onDrop = useCallback(async (acceptedFiles: File[]) => {
    const file = acceptedFiles[0];
    if (!file) return;

    setIsLoading(true);
    setUploadStatus('idle');
    setErrorMessage('');

    try {
      const text = await file.text();
      const userData = processCSVData(text);

      // Simulate ML processing delay
      await new Promise(resolve => setTimeout(resolve, 2000));

      const { scores, stats } = calculateRiskScore(userData);

      onFileProcessed(scores, stats);
      setUploadStatus('success');
    } catch (error) {
      setUploadStatus('error');
      setErrorMessage('Error processing file. Please check the format.');
    } finally {
      setIsLoading(false);
    }
  }, [onFileProcessed, setIsLoading]);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'text/csv': ['.csv'],
      'application/json': ['.json']
    },
    multiple: false
  });

  return (
    <div className="space-y-6">
      <div
        {...getRootProps()}
        className={`group relative border-2 border-dashed rounded-2xl p-12 text-center cursor-pointer transition-all duration-300 ${
          isDragActive
            ? 'border-brand-500 bg-brand-50 shadow-soft scale-[1.02]'
            : 'border-slate-300 hover:border-brand-400 hover:bg-slate-50 hover:shadow-soft'
        } ${isLoading ? 'pointer-events-none opacity-60' : ''}`}
      >
        <input {...getInputProps()} />

        {/* Background decoration */}
        <div className="absolute inset-0 rounded-2xl bg-gradient-to-br from-brand-50/50 to-primary-50/30 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>

        <div className="relative flex flex-col items-center space-y-6">
          <div className={`p-4 rounded-2xl transition-all duration-300 ${
            isDragActive ? 'bg-brand-100 scale-110' : 'bg-slate-100 group-hover:bg-brand-100 group-hover:scale-105'
          }`}>
            <Upload className={`h-12 w-12 transition-colors duration-300 ${
              isDragActive ? 'text-brand-600' : 'text-slate-400 group-hover:text-brand-500'
            }`} />
          </div>

          {isLoading ? (
            <div className="space-y-4">
              <div className="relative">
                <div className="animate-spin rounded-full h-10 w-10 border-3 border-brand-200 border-t-brand-600 mx-auto"></div>
                <div className="absolute inset-0 rounded-full border-3 border-brand-100 animate-pulse"></div>
              </div>
              <div className="space-y-2">
                <p className="text-lg font-semibold text-slate-700">Processing transaction data...</p>
                <p className="text-sm text-slate-500">Analyzing patterns and calculating risk scores</p>
              </div>
            </div>
          ) : (
            <div className="space-y-4">
              <div className="space-y-2">
                <h3 className={`text-xl font-bold transition-colors duration-300 ${
                  isDragActive ? 'text-brand-700' : 'text-slate-800 group-hover:text-brand-600'
                }`}>
                  {isDragActive ? 'Drop your file here' : 'Upload Transaction Data'}
                </h3>
                <p className="text-slate-600 font-medium">
                  Drag & drop a CSV or JSON file, or click to browse
                </p>
              </div>

              <div className="max-w-2xl mx-auto">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                  <div className="bg-white/80 backdrop-blur-sm rounded-xl p-4 border border-slate-200">
                    <h4 className="font-semibold text-slate-700 mb-2 flex items-center">
                      <div className="w-2 h-2 bg-green-500 rounded-full mr-2"></div>
                      Required Fields
                    </h4>
                    <ul className="text-slate-600 space-y-1 text-xs">
                      <li>• user_id</li>
                      <li>• income_inflow</li>
                      <li>• bill_payments_on_time</li>
                      <li>• avg_upi_txn</li>
                      <li>• recharge_freq</li>
                      <li>• loan_defaulted</li>
                    </ul>
                  </div>
                  <div className="bg-white/80 backdrop-blur-sm rounded-xl p-4 border border-slate-200">
                    <h4 className="font-semibold text-slate-700 mb-2 flex items-center">
                      <div className="w-2 h-2 bg-blue-500 rounded-full mr-2"></div>
                      Optional (RBI)
                    </h4>
                    <ul className="text-slate-600 space-y-1 text-xs">
                      <li>• existing_loan_amount</li>
                      <li>• household_income</li>
                      <li>• rural_urban_classification</li>
                      <li>• purpose_of_loan</li>
                      <li>• requested_amount</li>
                      <li>• proposed_tenure_months</li>
                    </ul>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Status Messages */}
      {uploadStatus === 'success' && (
        <div className="animate-slideInUp bg-gradient-to-r from-success-50 to-success-100 border border-success-200 rounded-2xl p-4 shadow-soft">
          <div className="flex items-center space-x-3">
            <div className="flex-shrink-0">
              <div className="w-10 h-10 bg-success-500 rounded-full flex items-center justify-center">
                <CheckCircle className="h-5 w-5 text-white" />
              </div>
            </div>
            <div>
              <h4 className="font-semibold text-success-800">Processing Complete!</h4>
              <p className="text-success-700 text-sm">Risk scores have been calculated and are ready for review.</p>
            </div>
          </div>
        </div>
      )}

      {uploadStatus === 'error' && (
        <div className="animate-slideInUp bg-gradient-to-r from-danger-50 to-danger-100 border border-danger-200 rounded-2xl p-4 shadow-soft">
          <div className="flex items-center space-x-3">
            <div className="flex-shrink-0">
              <div className="w-10 h-10 bg-danger-500 rounded-full flex items-center justify-center">
                <AlertCircle className="h-5 w-5 text-white" />
              </div>
            </div>
            <div>
